import React, { useEffect, useRef, useState } from 'react';
import io from 'socket.io-client';
import { v4 as uuidv4 } from 'uuid';
import html2canvas from 'html2canvas';

const SIGNAL_SERVER = 'http://localhost:5000';

function Room({ roomId }) {
  const [participants, setParticipants] = useState([]);
  const [remoteStreams, setRemoteStreams] = useState({}); // userId -> MediaStream
  const [micOn, setMicOn] = useState(true);
  const [camOn, setCamOn] = useState(true);
  const [screenSharing, setScreenSharing] = useState(false);
  const [error, setError] = useState('');
  const [showPermissionPrompt, setShowPermissionPrompt] = useState(true);
  const localVideoRef = useRef();
  const socketRef = useRef();
  const peersRef = useRef({}); // userId -> RTCPeerConnection
  const userId = useRef(uuidv4());
  const localStream = useRef();
  const screenStream = useRef();

  useEffect(() => {
    socketRef.current = io(SIGNAL_SERVER);
    socketRef.current.emit('join', { roomId, userId: userId.current });
    socketRef.current.on('participants', (list) => {
      setParticipants(list);
      // Create peer connections to all existing participants (except self)
      list.forEach(existingId => {
        if (existingId !== userId.current && !peersRef.current[existingId]) {
          const peer = createPeer(existingId, true);
          peersRef.current[existingId] = peer;
        }
      });
    });
    socketRef.current.on('signal', handleSignal);
    socketRef.current.on('error', setError);
    socketRef.current.on('new-user', ({ userId: newUserId }) => {
      // All existing users create a peer connection to the new user
      if (newUserId !== userId.current && !peersRef.current[newUserId]) {
        const peer = createPeer(newUserId, true);
        peersRef.current[newUserId] = peer;
      }
    });
    return () => {
      socketRef.current.emit('leave', { roomId, userId: userId.current });
      socketRef.current.disconnect();
      Object.values(peersRef.current).forEach(peer => peer.close());
    };
    // eslint-disable-next-line
  }, []);

  useEffect(() => {
    if (!roomId) return;
    setShowPermissionPrompt(true);
    // Do not call startLocalStream here!
    // Request camera/mic permissions and start local stream only when joining a new meeting
    // eslint-disable-next-line
  }, [roomId]);

  const handleAllowPermissions = async () => {
    setShowPermissionPrompt(false);
    await startLocalStream();
  };

  const startLocalStream = async () => {
    try {
      localStream.current = await navigator.mediaDevices.getUserMedia({ video: true, audio: true });
    } catch (e) {
      setError('Could not access camera/mic');
    }
  };

  // Handle new user: create a peer connection for them
  const handleNewUser = async ({ userId: newUserId }) => {
    if (newUserId === userId.current) return;
    if (!peersRef.current[newUserId]) {
      const peer = createPeer(newUserId, true);
      peersRef.current[newUserId] = peer;
    }
  };

  // Helper to determine if we are polite (lexicographically greater userId)
  const isPolite = (otherUserId) => userId.current > otherUserId;

  // Create peer connection for a user
  const createPeer = (otherUserId, initiator) => {
    const peer = new window.RTCPeerConnection();
    if (localStream.current) {
      localStream.current.getTracks().forEach(track => peer.addTrack(track, localStream.current));
    }
    peer.ontrack = (e) => {
      setRemoteStreams(prev => ({
        ...prev,
        [otherUserId]: e.streams[0]
      }));
    };
    peer.onicecandidate = (e) => {
      if (e.candidate) {
        socketRef.current.emit('signal', { roomId, userId: userId.current, to: otherUserId, signal: { candidate: e.candidate } });
      }
    };
    if (initiator) {
      peer.createOffer().then(offer => {
        peer.setLocalDescription(offer);
        socketRef.current.emit('signal', { roomId, userId: userId.current, to: otherUserId, signal: { sdp: offer } });
      });
    }
    return peer;
  };

  // Handle incoming signal with polite peer logic
  const handleSignal = async ({ userId: from, signal }) => {
    if (from === userId.current) return;
    let peer = peersRef.current[from];
    if (!peer) {
      peer = createPeer(from, false);
      peersRef.current[from] = peer;
    }
    try {
      if (signal.sdp) {
        const desc = new RTCSessionDescription(signal.sdp);
        const isOffer = desc.type === 'offer';
        const polite = isPolite(from);

        if (isOffer) {
          const signalingState = peer.signalingState;
          if (signalingState !== 'stable') {
            if (!polite) {
              // Impolite peer ignores glare offer
              return;
            }
            // Polite peer rolls back and accepts the offer
            await peer.setLocalDescription({ type: 'rollback' });
          }
          await peer.setRemoteDescription(desc);
          const answer = await peer.createAnswer();
          await peer.setLocalDescription(answer);
          socketRef.current.emit('signal', { roomId, userId: userId.current, to: from, signal: { sdp: answer } });
        } else {
          await peer.setRemoteDescription(desc);
        }
      } else if (signal.candidate) {
        try {
          await peer.addIceCandidate(new RTCIceCandidate(signal.candidate));
        } catch (err) {
          // Ignore ICE candidate errors
        }
      }
    } catch (err) {
      // Optionally log or handle error
    }
  };

  const toggleMic = () => {
    localStream.current.getAudioTracks().forEach(track => (track.enabled = !micOn));
    setMicOn(!micOn);
  };

  const toggleCam = () => {
    localStream.current.getVideoTracks().forEach(track => (track.enabled = !camOn));
    setCamOn(!camOn);
  };

  const leaveRoom = () => {
    socketRef.current.emit('leave', { roomId, userId: userId.current });
    window.location.reload();
  };

  const startScreenShare = async () => {
    try {
      screenStream.current = await navigator.mediaDevices.getDisplayMedia({ video: true });
      const screenTrack = screenStream.current.getVideoTracks()[0];
      const sender = peerRef.current.getSenders().find(s => s.track.kind === 'video');
      sender.replaceTrack(screenTrack);
      setScreenSharing(true);
      screenTrack.onended = () => {
        sender.replaceTrack(localStream.current.getVideoTracks()[0]);
        setScreenSharing(false);
      };
    } catch (e) {
      setError('Screen sharing failed');
    }
  };

  const takeScreenshot = async () => {
    if (!localVideoRef.current) return;
    const canvas = await html2canvas(localVideoRef.current);
    const link = document.createElement('a');
    link.href = canvas.toDataURL('image/png');
    link.download = 'screenshot.png';
    link.click();
  };

  // Helper for initials
  const getInitials = name =>
    name
      .split(' ')
      .map(n => n[0])
      .join('')
      .toUpperCase();

  // Responsive grid columns
  const getGridColumns = () => {
    if (participants.length <= 2) return 'repeat(2, 1fr)';
    if (participants.length <= 4) return 'repeat(2, 1fr)';
    if (participants.length <= 6) return 'repeat(3, 1fr)';
    if (participants.length <= 9) return 'repeat(3, 1fr)';
    return 'repeat(4, 1fr)';
  };

  // Compose participant list for grid (local + remote)
  const participantGrid = [
    {
      id: userId.current,
      name: 'You',
      cameraOn: camOn && localStream.current,
      isLocal: true,
      avatarUrl: null,
      stream: localStream.current,
    },
    ...participants
      .filter(p => p !== userId.current && typeof p === 'string')
      .map(name => ({
        id: name,
        name,
        cameraOn: !!remoteStreams[name],
        isLocal: false,
        avatarUrl: null,
        stream: remoteStreams[name] || null,
      })),
  ];

  // Render participant tile
  const renderParticipant = (user, idx) => (
    <div
      key={user.id || user.name || idx}
      style={{
        background: '#1a1a1a',
        borderRadius: 8,
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        minWidth: 200,
        width: '100%',
        height: 150,
        position: 'relative',
        overflow: 'hidden',
        border: user.isLocal ? '2px solid #0078d4' : '1px solid #323130',
        boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
      }}
    >
      {/* Show video if camera is on and stream is available */}
      {user.cameraOn && user.stream && (
        <video
          ref={el => {
            if (el && user.stream) el.srcObject = user.stream;
          }}
          autoPlay
          muted={user.isLocal}
          playsInline
          style={{
            width: '100%',
            height: '100%',
            objectFit: 'cover',
            background: '#000',
          }}
        />
      )}
      {/* If camera is off or no stream, show avatar */}
      {(!user.cameraOn || !user.stream) && (
        <div style={{
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          justifyContent: 'center',
          height: '100%',
          color: '#fff'
        }}>
          {user.avatarUrl ? (
            <img
              src={user.avatarUrl}
              alt={user.name}
              style={{
                width: 64,
                height: 64,
                borderRadius: '50%',
                marginBottom: 8,
                objectFit: 'cover',
                background: '#605e5c',
              }}
            />
          ) : (
            <div
              style={{
                width: 64,
                height: 64,
                borderRadius: '50%',
                marginBottom: 8,
                background: '#0078d4',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: 24,
                fontWeight: 600,
                color: '#fff',
              }}
            >
              {getInitials(user.name || '')}
            </div>
          )}
        </div>
      )}
      
      {/* Status indicators */}
      <div style={{
        position: 'absolute',
        top: 8,
        right: 8,
        display: 'flex',
        gap: 4,
      }}>
        {!user.isLocal && (
          <div style={{
            width: 24,
            height: 24,
            borderRadius: '50%',
            background: 'rgba(0,0,0,0.6)',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontSize: 12,
          }}>
            üé§
          </div>
        )}
        {user.isLocal && !micOn && (
          <div style={{
            width: 24,
            height: 24,
            borderRadius: '50%',
            background: 'rgba(255,0,0,0.8)',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontSize: 12,
          }}>
            üîá
          </div>
        )}
      </div>

      {/* Name overlay */}
      <div
        style={{
          position: 'absolute',
          bottom: 0,
          left: 0,
          right: 0,
          background: 'linear-gradient(transparent, rgba(0,0,0,0.7))',
          color: '#fff',
          padding: '16px 12px 8px',
          fontSize: 14,
          fontWeight: 500,
        }}
      >
        {user.name}
      </div>
    </div>
  );

  useEffect(() => {
    if (!localStream.current) return;
    // For each peer, add tracks if not already present and renegotiate
    Object.entries(peersRef.current).forEach(([peerId, peer]) => {
      // Add tracks if not already present
      const senders = peer.getSenders();
      localStream.current.getTracks().forEach(track => {
        if (!senders.find(sender => sender.track && sender.track.kind === track.kind)) {
          peer.addTrack(track, localStream.current);
        }
      });
      // Renegotiate (create and send new offer)
      peer.createOffer().then(offer => {
        peer.setLocalDescription(offer);
        socketRef.current.emit('signal', { roomId, userId: userId.current, to: peerId, signal: { sdp: offer } });
      });
    });
  }, [localStream.current]);

  const controlButtonStyle = {
    width: 48,
    height: 48,
    borderRadius: '50%',
    border: 'none',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    fontSize: 18,
    cursor: 'pointer',
    transition: 'all 0.2s ease',
    fontWeight: 500,
  };

  return (
    <div style={{ 
      minHeight: '100vh', 
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      display: 'flex',
      flexDirection: 'column',
      fontFamily: '"Segoe UI", Tahoma, Geneva, Verdana, sans-serif'
    }}>
      {/* Permission Modal */}
      {showPermissionPrompt && (
        <div style={{
          position: 'fixed', 
          top: 0, 
          left: 0, 
          width: '100vw', 
          height: '100vh',
          background: 'rgba(0,0,0,0.8)', 
          zIndex: 1000, 
          display: 'flex', 
          alignItems: 'center', 
          justifyContent: 'center'
        }}>
          <div style={{
            background: '#fff', 
            borderRadius: 12, 
            padding: 40, 
            boxShadow: '0 8px 32px rgba(0,0,0,0.3)', 
            maxWidth: 400, 
            textAlign: 'center'
          }}>
            <div style={{ fontSize: 48, marginBottom: 20 }}>üé•</div>
            <h2 style={{ margin: '0 0 16px 0', color: '#323130', fontSize: 24 }}>Join Meeting</h2>
            <p style={{ margin: '0 0 24px 0', color: '#605e5c', lineHeight: 1.5 }}>
              We need access to your camera and microphone to connect you with others in this meeting.
            </p>
            <button 
              onClick={handleAllowPermissions} 
              style={{ 
                background: '#0078d4',
                color: 'white',
                border: 'none',
                borderRadius: 4,
                fontSize: 16, 
                fontWeight: 600,
                padding: '12px 32px',
                cursor: 'pointer',
                transition: 'background 0.2s'
              }}
              onMouseOver={(e) => e.target.style.background = '#106ebe'}
              onMouseOut={(e) => e.target.style.background = '#0078d4'}
            >
              Join Now
            </button>
          </div>
        </div>
      )}

      {/* Header */}
      <div style={{
        background: 'rgba(255,255,255,0.1)',
        backdropFilter: 'blur(10px)',
        padding: '16px 24px',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
        borderBottom: '1px solid rgba(255,255,255,0.1)'
      }}>
        <div style={{ display: 'flex', alignItems: 'center', gap: 12 }}>
          <div style={{ 
            width: 40, 
            height: 40, 
            background: '#0078d4', 
            borderRadius: 8,
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontSize: 20
          }}>
            üìπ
          </div>
          <div>
            <h1 style={{ margin: 0, color: '#fff', fontSize: 20, fontWeight: 600 }}>
              Meeting Room
            </h1>
            <p style={{ margin: 0, color: 'rgba(255,255,255,0.8)', fontSize: 14 }}>
              Room ID: {roomId}
            </p>
          </div>
        </div>
        <div style={{ color: 'rgba(255,255,255,0.9)', fontSize: 14 }}>
          {participantGrid.length} participant{participantGrid.length !== 1 ? 's' : ''}
        </div>
      </div>

      {/* Main content area */}
      <div style={{ flex: 1, display: 'flex', flexDirection: 'column', padding: 24 }}>
        {/* Video grid */}
        <div
          style={{
            display: 'grid',
            gridTemplateColumns: getGridColumns(),
            gap: 16,
            alignItems: 'center',
            justifyItems: 'center',
            maxWidth: 1200,
            margin: '0 auto',
            width: '100%',
            flex: 1,
          }}
        >
          {participantGrid.length === 0 && (
            <div style={{ 
              color: 'rgba(255,255,255,0.8)', 
              textAlign: 'center', 
              gridColumn: '1/-1',
              fontSize: 18 
            }}>
              Waiting for participants to join...
            </div>
          )}
          {participantGrid.map(renderParticipant)}
        </div>
      </div>

      {/* Controls bar */}
      <div style={{
        background: 'rgba(0,0,0,0.8)',
        backdropFilter: 'blur(10px)',
        padding: '16px 24px',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        gap: 16,
        borderTop: '1px solid rgba(255,255,255,0.1)'
      }}>
        <button 
          onClick={toggleMic}
          style={{
            ...controlButtonStyle,
            background: micOn ? 'rgba(255,255,255,0.2)' : '#d13438',
            color: '#fff'
          }}
          onMouseOver={(e) => e.target.style.background = micOn ? 'rgba(255,255,255,0.3)' : '#b52d31'}
          onMouseOut={(e) => e.target.style.background = micOn ? 'rgba(255,255,255,0.2)' : '#d13438'}
          title={micOn ? 'Mute microphone' : 'Unmute microphone'}
        >
          {micOn ? 'üé§' : 'üîá'}
        </button>

        <button 
          onClick={toggleCam}
          style={{
            ...controlButtonStyle,
            background: camOn ? 'rgba(255,255,255,0.2)' : '#d13438',
            color: '#fff'
          }}
          onMouseOver={(e) => e.target.style.background = camOn ? 'rgba(255,255,255,0.3)' : '#b52d31'}
          onMouseOut={(e) => e.target.style.background = camOn ? 'rgba(255,255,255,0.2)' : '#d13438'}
          title={camOn ? 'Turn off camera' : 'Turn on camera'}
        >
          {camOn ? 'üìπ' : 'üì∑'}
        </button>

        <button 
          onClick={startScreenShare}
          disabled={screenSharing}
          style={{
            ...controlButtonStyle,
            background: screenSharing ? '#0078d4' : 'rgba(255,255,255,0.2)',
            color: '#fff',
            opacity: screenSharing ? 1 : 0.9
          }}
          onMouseOver={(e) => !screenSharing && (e.target.style.background = 'rgba(255,255,255,0.3)')}
          onMouseOut={(e) => !screenSharing && (e.target.style.background = 'rgba(255,255,255,0.2)')}
          title="Share screen"
        >
          üñ•Ô∏è
        </button>

        <button 
          onClick={takeScreenshot}
          style={{
            ...controlButtonStyle,
            background: 'rgba(255,255,255,0.2)',
            color: '#fff'
          }}
          onMouseOver={(e) => e.target.style.background = 'rgba(255,255,255,0.3)'}
          onMouseOut={(e) => e.target.style.background = 'rgba(255,255,255,0.2)'}
          title="Take screenshot"
        >
          üì∏
        </button>

        <div style={{ width: 1, height: 32, background: 'rgba(255,255,255,0.2)', margin: '0 8px' }} />

        <button 
          onClick={leaveRoom}
          style={{
            ...controlButtonStyle,
            background: '#d13438',
            color: '#fff'
          }}
          onMouseOver={(e) => e.target.style.background = '#b52d31'}
          onMouseOut={(e) => e.target.style.background = '#d13438'}
          title="Leave meeting"
        >
          üìû
        </button>
      </div>

      {/* Error notification */}
      {error && (
        <div style={{
          position: 'fixed',
          top: 80,
          right: 24,
          background: '#d13438',
          color: '#fff',
          padding: '12px 16px',
          borderRadius: 8,
          fontSize: 14,
          fontWeight: 500,
          boxShadow: '0 4px 12px rgba(0,0,0,0.3)',
          zIndex: 1000
        }}>
          {error}
        </div>
      )}
    </div>
  );
}

export default Room;
