import React, { useEffect, useRef, useState } from 'react';
import io from 'socket.io-client';
import { v4 as uuidv4 } from 'uuid';
import html2canvas from 'html2canvas';

const SIGNAL_SERVER = 'http://localhost:5000';

function Room({ roomId }) {
  const [participants, setParticipants] = useState([]);
  const [micOn, setMicOn] = useState(true);
  const [camOn, setCamOn] = useState(true);
  const [screenSharing, setScreenSharing] = useState(false);
  const [error, setError] = useState('');
  const localVideoRef = useRef();
  const remoteVideoRef = useRef();
  const socketRef = useRef();
  const peerRef = useRef();
  const userId = useRef(uuidv4());
  const localStream = useRef();
  const screenStream = useRef();

  useEffect(() => {
    socketRef.current = io(SIGNAL_SERVER);
    socketRef.current.emit('join', { roomId, userId: userId.current });
    socketRef.current.on('participants', setParticipants);
    socketRef.current.on('signal', handleSignal);
    socketRef.current.on('error', setError);
    return () => {
      socketRef.current.emit('leave', { roomId, userId: userId.current });
      socketRef.current.disconnect();
      if (peerRef.current) peerRef.current.close();
    };
    // eslint-disable-next-line
  }, []);

  useEffect(() => {
    startLocalStream();
    // eslint-disable-next-line
  }, []);

  const startLocalStream = async () => {
    try {
      localStream.current = await navigator.mediaDevices.getUserMedia({ video: true, audio: true });
      if (localVideoRef.current) {
        localVideoRef.current.srcObject = localStream.current;
      }
      createPeer();
    } catch (e) {
      setError('Could not access camera/mic');
    }
  };

  const createPeer = () => {
    const peer = new window.RTCPeerConnection();
    localStream.current.getTracks().forEach(track => peer.addTrack(track, localStream.current));
    peer.ontrack = (e) => {
      if (remoteVideoRef.current) {
        remoteVideoRef.current.srcObject = e.streams[0];
      }
    };
    peer.onicecandidate = (e) => {
      if (e.candidate) {
        socketRef.current.emit('signal', { roomId, userId: userId.current, signal: { candidate: e.candidate } });
      }
    };
    peerRef.current = peer;
  };

  const handleSignal = async ({ userId: from, signal }) => {
    if (signal.sdp) {
      await peerRef.current.setRemoteDescription(new RTCSessionDescription(signal.sdp));
      if (signal.sdp.type === 'offer') {
        const answer = await peerRef.current.createAnswer();
        await peerRef.current.setLocalDescription(answer);
        socketRef.current.emit('signal', { roomId, userId: userId.current, signal: { sdp: answer } });
      }
    } else if (signal.candidate) {
      await peerRef.current.addIceCandidate(new RTCIceCandidate(signal.candidate));
    }
  };

  const startCall = async () => {
    const offer = await peerRef.current.createOffer();
    await peerRef.current.setLocalDescription(offer);
    socketRef.current.emit('signal', { roomId, userId: userId.current, signal: { sdp: offer } });
  };

  const toggleMic = () => {
    localStream.current.getAudioTracks().forEach(track => (track.enabled = !micOn));
    setMicOn(!micOn);
  };

  const toggleCam = () => {
    localStream.current.getVideoTracks().forEach(track => (track.enabled = !camOn));
    setCamOn(!camOn);
  };

  const leaveRoom = () => {
    socketRef.current.emit('leave', { roomId, userId: userId.current });
    window.location.reload();
  };

  const startScreenShare = async () => {
    try {
      screenStream.current = await navigator.mediaDevices.getDisplayMedia({ video: true });
      const screenTrack = screenStream.current.getVideoTracks()[0];
      const sender = peerRef.current.getSenders().find(s => s.track.kind === 'video');
      sender.replaceTrack(screenTrack);
      setScreenSharing(true);
      screenTrack.onended = () => {
        sender.replaceTrack(localStream.current.getVideoTracks()[0]);
        setScreenSharing(false);
      };
    } catch (e) {
      setError('Screen sharing failed');
    }
  };

  const takeScreenshot = async () => {
    if (!localVideoRef.current) return;
    const canvas = await html2canvas(localVideoRef.current);
    const link = document.createElement('a');
    link.href = canvas.toDataURL('image/png');
    link.download = 'screenshot.png';
    link.click();
  };

  // Helper for initials
  const getInitials = name =>
    name
      .split(' ')
      .map(n => n[0])
      .join('')
      .toUpperCase();

  // Responsive grid columns
  const getGridColumns = () => {
    if (participants.length <= 2) return 'repeat(2, 1fr)';
    if (participants.length <= 4) return 'repeat(2, 1fr)';
    if (participants.length <= 6) return 'repeat(3, 1fr)';
    if (participants.length <= 9) return 'repeat(3, 1fr)';
    return 'repeat(4, 1fr)';
  };

  // Render participant tile
  const renderParticipant = (user, idx) => (
    <div
      key={user.id || user.name || idx}
      style={{
        background: '#f3f3f7',
        borderRadius: 12,
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        minWidth: 120,
        maxWidth: 180,
        width: '100%',
        padding: 16,
        boxShadow: '0 1px 4px #0001',
        position: 'relative',
        aspectRatio: '4/3',
      }}
    >
      {/* Show video if camera is on and it's the local user or remote user */}
      {user.cameraOn && user.isLocal && (
        <video
          ref={localVideoRef}
          autoPlay
          muted
          playsInline
          style={{
            width: '100%',
            height: '100%',
            borderRadius: 10,
            objectFit: 'cover',
            background: '#000',
          }}
        />
      )}
      {user.cameraOn && !user.isLocal && (
        <video
          ref={remoteVideoRef}
          autoPlay
          playsInline
          style={{
            width: '100%',
            height: '100%',
            borderRadius: 10,
            objectFit: 'cover',
            background: '#000',
          }}
        />
      )}
      {/* If camera is off, show avatar */}
      {!user.cameraOn && (
        <>
          {user.avatarUrl ? (
            <img
              src={user.avatarUrl}
              alt={user.name}
              style={{
                width: 56,
                height: 56,
                borderRadius: '50%',
                marginBottom: 10,
                objectFit: 'cover',
                background: '#bbb',
              }}
            />
          ) : (
            <div
              style={{
                width: 56,
                height: 56,
                borderRadius: '50%',
                marginBottom: 10,
                background: '#bbb',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: 22,
                color: '#fff',
              }}
            >
              {getInitials(user.name || '')}
            </div>
          )}
        </>
      )}
      {/* Name overlay */}
      <div
        style={{
          position: 'absolute',
          bottom: 8,
          left: 0,
          right: 0,
          textAlign: 'center',
          fontWeight: 'bold',
          fontSize: 15,
          color: '#222',
          background: user.cameraOn ? 'rgba(255,255,255,0.7)' : 'transparent',
          borderRadius: 6,
          margin: '0 12px',
          padding: '2px 0',
        }}
      >
        {user.name}
      </div>
    </div>
  );

  // Compose participant list for grid (local + remote)
  // For demo: local user is always first, remote user is second
  const participantGrid = [
    {
      id: userId.current,
      name: 'You',
      cameraOn: camOn,
      isLocal: true,
      avatarUrl: null,
    },
    // Add remote participants (simulate cameraOn as true if you want to show remote video)
    ...participants
      .filter(p => p !== userId.current && typeof p === 'string')
      .map(name => ({
        id: name,
        name,
        cameraOn: true, // or false if you want to show avatar
        isLocal: false,
        avatarUrl: null,
      })),
  ];

  return (
    <div style={{ minHeight: '100vh', background: '#f7f7fa', padding: 0 }}>
      <h2 style={{ textAlign: 'center', margin: 0, padding: 16 }}>Room: {roomId}</h2>
      {/* Teams-style participant grid */}
      <div
        style={{
          display: 'grid',
          gridTemplateColumns: getGridColumns(),
          gap: 24,
          padding: 24,
          alignItems: 'center',
          justifyItems: 'center',
          maxWidth: 1200,
          margin: '0 auto',
        }}
      >
        {participantGrid.length === 0 && (
          <div style={{ color: '#888', textAlign: 'center', gridColumn: '1/-1' }}>No participants yet</div>
        )}
        {participantGrid.map(renderParticipant)}
      </div>
      {/* Controls */}
      <div style={{ display: 'flex', justifyContent: 'center', gap: 12, margin: 24 }}>
        <button onClick={toggleMic}>{micOn ? 'Mute Mic' : 'Unmute Mic'}</button>
        <button onClick={toggleCam}>{camOn ? 'Turn Off Cam' : 'Turn On Cam'}</button>
        <button onClick={startCall}>Start Call</button>
        <button onClick={leaveRoom}>Leave Room</button>
        <button onClick={startScreenShare} disabled={screenSharing}>Share Screen</button>
        <button onClick={takeScreenshot}>Screenshot</button>
      </div>
      {error && <div style={{ color: 'red', textAlign: 'center' }}>{error}</div>}
    </div>
  );
}

export default Room;
