import React, { useEffect, useRef, useState } from 'react';
import io from 'socket.io-client';
import { v4 as uuidv4 } from 'uuid';
import html2canvas from 'html2canvas';

const SIGNAL_SERVER = 'http://localhost:5000';

function Room({ roomId }) {
  const [participants, setParticipants] = useState([]);
  const [remoteStreams, setRemoteStreams] = useState({}); // userId -> MediaStream
  const [micOn, setMicOn] = useState(true);
  const [camOn, setCamOn] = useState(true);
  const [screenSharing, setScreenSharing] = useState(false);
  const [error, setError] = useState('');
  const [showPermissionPrompt, setShowPermissionPrompt] = useState(true);
  const localVideoRef = useRef();
  const socketRef = useRef();
  const peersRef = useRef({}); // userId -> RTCPeerConnection
  const userId = useRef(uuidv4());
  const localStream = useRef();
  const screenStream = useRef();

  useEffect(() => {
    socketRef.current = io(SIGNAL_SERVER);
    socketRef.current.emit('join', { roomId, userId: userId.current });
    socketRef.current.on('participants', (list) => {
      setParticipants(list);
      // Create peer connections to all existing participants (except self)
      list.forEach(existingId => {
        if (existingId !== userId.current && !peersRef.current[existingId]) {
          const peer = createPeer(existingId, true);
          peersRef.current[existingId] = peer;
        }
      });
    });
    socketRef.current.on('signal', handleSignal);
    socketRef.current.on('error', setError);
    socketRef.current.on('new-user', ({ userId: newUserId }) => {
      // All existing users create a peer connection to the new user
      if (newUserId !== userId.current && !peersRef.current[newUserId]) {
        const peer = createPeer(newUserId, true);
        peersRef.current[newUserId] = peer;
      }
    });
    return () => {
      socketRef.current.emit('leave', { roomId, userId: userId.current });
      socketRef.current.disconnect();
      Object.values(peersRef.current).forEach(peer => peer.close());
    };
    // eslint-disable-next-line
  }, []);

  useEffect(() => {
    if (!roomId) return;
    setShowPermissionPrompt(true);
    // Do not call startLocalStream here!
    // Request camera/mic permissions and start local stream only when joining a new meeting
    // eslint-disable-next-line
  }, [roomId]);

  const handleAllowPermissions = async () => {
    setShowPermissionPrompt(false);
    await startLocalStream();
  };

  const startLocalStream = async () => {
    try {
      localStream.current = await navigator.mediaDevices.getUserMedia({ video: true, audio: true });
    } catch (e) {
      setError('Could not access camera/mic');
    }
  };

  // Handle new user: create a peer connection for them
  const handleNewUser = async ({ userId: newUserId }) => {
    if (newUserId === userId.current) return;
    if (!peersRef.current[newUserId]) {
      const peer = createPeer(newUserId, true);
      peersRef.current[newUserId] = peer;
    }
  };

  // Helper to determine if we are polite (lexicographically greater userId)
  const isPolite = (otherUserId) => userId.current > otherUserId;

  // Create peer connection for a user
  const createPeer = (otherUserId, initiator) => {
    const peer = new window.RTCPeerConnection();
    if (localStream.current) {
      localStream.current.getTracks().forEach(track => peer.addTrack(track, localStream.current));
    }
    peer.ontrack = (e) => {
      setRemoteStreams(prev => ({
        ...prev,
        [otherUserId]: e.streams[0]
      }));
    };
    peer.onicecandidate = (e) => {
      if (e.candidate) {
        socketRef.current.emit('signal', { roomId, userId: userId.current, to: otherUserId, signal: { candidate: e.candidate } });
      }
    };
    if (initiator) {
      peer.createOffer().then(offer => {
        peer.setLocalDescription(offer);
        socketRef.current.emit('signal', { roomId, userId: userId.current, to: otherUserId, signal: { sdp: offer } });
      });
    }
    return peer;
  };

  // Handle incoming signal with polite peer logic
  const handleSignal = async ({ userId: from, signal }) => {
    if (from === userId.current) return;
    let peer = peersRef.current[from];
    if (!peer) {
      peer = createPeer(from, false);
      peersRef.current[from] = peer;
    }
    try {
      if (signal.sdp) {
        const desc = new RTCSessionDescription(signal.sdp);
        const isOffer = desc.type === 'offer';
        const polite = isPolite(from);

        if (isOffer) {
          const signalingState = peer.signalingState;
          if (signalingState !== 'stable') {
            if (!polite) {
              // Impolite peer ignores glare offer
              return;
            }
            // Polite peer rolls back and accepts the offer
            await peer.setLocalDescription({ type: 'rollback' });
          }
          await peer.setRemoteDescription(desc);
          const answer = await peer.createAnswer();
          await peer.setLocalDescription(answer);
          socketRef.current.emit('signal', { roomId, userId: userId.current, to: from, signal: { sdp: answer } });
        } else {
          await peer.setRemoteDescription(desc);
        }
      } else if (signal.candidate) {
        try {
          await peer.addIceCandidate(new RTCIceCandidate(signal.candidate));
        } catch (err) {
          // Ignore ICE candidate errors
        }
      }
    } catch (err) {
      // Optionally log or handle error
    }
  };

  const toggleMic = () => {
    localStream.current.getAudioTracks().forEach(track => (track.enabled = !micOn));
    setMicOn(!micOn);
  };

  const toggleCam = () => {
    localStream.current.getVideoTracks().forEach(track => (track.enabled = !camOn));
    setCamOn(!camOn);
  };

  const leaveRoom = () => {
    socketRef.current.emit('leave', { roomId, userId: userId.current });
    window.location.reload();
  };

  const startScreenShare = async () => {
    try {
      screenStream.current = await navigator.mediaDevices.getDisplayMedia({ video: true });
      const screenTrack = screenStream.current.getVideoTracks()[0];
      const sender = peerRef.current.getSenders().find(s => s.track.kind === 'video');
      sender.replaceTrack(screenTrack);
      setScreenSharing(true);
      screenTrack.onended = () => {
        sender.replaceTrack(localStream.current.getVideoTracks()[0]);
        setScreenSharing(false);
      };
    } catch (e) {
      setError('Screen sharing failed');
    }
  };

  const takeScreenshot = async () => {
    if (!localVideoRef.current) return;
    const canvas = await html2canvas(localVideoRef.current);
    const link = document.createElement('a');
    link.href = canvas.toDataURL('image/png');
    link.download = 'screenshot.png';
    link.click();
  };

  // Helper for initials
  const getInitials = name =>
    name
      .split(' ')
      .map(n => n[0])
      .join('')
      .toUpperCase();

  // Responsive grid columns
  const getGridColumns = () => {
    const count = participantGrid.length;
    if (count === 1) return '1fr';
    if (count === 2) return 'repeat(2, 1fr)';
    if (count <= 4) return 'repeat(2, 1fr)';
    if (count <= 6) return 'repeat(3, 1fr)';
    if (count <= 9) return 'repeat(3, 1fr)';
    return 'repeat(4, 1fr)';
  };

  // Compose participant list for grid (local + remote)
  const participantGrid = [
    {
      id: userId.current,
      name: 'You',
      cameraOn: camOn && localStream.current,
      isLocal: true,
      avatarUrl: null,
      stream: localStream.current,
    },
    ...participants
      .filter(p => p !== userId.current && typeof p === 'string')
      .map(name => ({
        id: name,
        name,
        cameraOn: !!remoteStreams[name],
        isLocal: false,
        avatarUrl: null,
        stream: remoteStreams[name] || null,
      })),
  ];

  // Render participant tile
  const renderParticipant = (user, idx) => (
    <div
      key={user.id || user.name || idx}
      style={{
        background: '#292929',
        border: user.isLocal ? '2px solid #0078d4' : '1px solid #464646',
        borderRadius: 8,
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        minWidth: participantGrid.length === 1 ? 640 : 320,
        minHeight: participantGrid.length === 1 ? 480 : 240,
        width: '100%',
        position: 'relative',
        overflow: 'hidden',
      }}
    >
      {/* Show video if camera is on and stream is available */}
      {user.cameraOn && user.stream && (
        <video
          ref={el => {
            if (el && user.stream) {
              el.srcObject = user.stream;
              if (user.isLocal) localVideoRef.current = el;
            }
          }}
          autoPlay
          muted={user.isLocal}
          playsInline
          style={{
            width: '100%',
            height: '100%',
            objectFit: 'cover',
          }}
        />
      )}
      
      {/* If camera is off or no stream, show avatar */}
      {(!user.cameraOn || !user.stream) && (
        <div style={{
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          justifyContent: 'center',
          height: '100%',
          color: '#ffffff'
        }}>
          <div
            style={{
              width: participantGrid.length === 1 ? 120 : 80,
              height: participantGrid.length === 1 ? 120 : 80,
              borderRadius: '50%',
              marginBottom: 16,
              background: '#605e5c',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              fontSize: participantGrid.length === 1 ? 36 : 28,
              fontWeight: 600,
              color: '#ffffff',
            }}
          >
            {getInitials(user.name || '')}
          </div>
          <div style={{
            fontSize: 14,
            color: '#c4c4c4',
            textAlign: 'center'
          }}>
            Camera turned off
          </div>
        </div>
      )}
      
      {/* Status indicators */}
      <div style={{
        position: 'absolute',
        top: 12,
        left: 12,
        display: 'flex',
        gap: 8,
      }}>
        {user.isLocal && !micOn && (
          <div style={{
            width: 28,
            height: 28,
            borderRadius: '50%',
            background: '#cc4125',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
          }}>
            <svg width="14" height="14" viewBox="0 0 24 24" fill="white">
              <path d="M16.5 12c0-1.77-1.02-3.29-2.5-4.03v2.21l2.45 2.45c.03-.2.05-.41.05-.63zm2.5 0c0 .94-.2 1.82-.54 2.64l1.51 1.51C20.63 14.91 21 13.5 21 12c0-4.28-2.99-7.86-7-8.77v2.06c2.89.86 5 3.54 5 6.71zM4.27 3L3 4.27 7.73 9H3v6h4l5 5v-6.73l4.25 4.25c-.67.52-1.42.93-2.25 1.18v2.06c1.38-.31 2.63-.95 3.69-1.81L19.73 21 21 19.73l-9-9L4.27 3zM12 4L9.91 6.09 12 8.18V4z"/>
            </svg>
          </div>
        )}
      </div>

      {/* Name label */}
      <div
        style={{
          position: 'absolute',
          bottom: 12,
          left: 12,
          background: 'rgba(0, 0, 0, 0.8)',
          color: '#ffffff',
          padding: '4px 8px',
          borderRadius: 4,
          fontSize: 14,
          fontWeight: 600,
        }}
      >
        {user.name}
      </div>
    </div>
  );

  useEffect(() => {
    if (!localStream.current) return;
    // For each peer, add tracks if not already present and renegotiate
    Object.entries(peersRef.current).forEach(([peerId, peer]) => {
      // Add tracks if not already present
      const senders = peer.getSenders();
      localStream.current.getTracks().forEach(track => {
        if (!senders.find(sender => sender.track && sender.track.kind === track.kind)) {
          peer.addTrack(track, localStream.current);
        }
      });
      // Renegotiate (create and send new offer)
      peer.createOffer().then(offer => {
        peer.setLocalDescription(offer);
        socketRef.current.emit('signal', { roomId, userId: userId.current, to: peerId, signal: { sdp: offer } });
      });
    });
  }, [localStream.current]);

  return (
    <div style={{ 
      minHeight: '100vh', 
      background: '#1e1e1e',
      display: 'flex',
      flexDirection: 'column',
      fontFamily: '"Segoe UI", Tahoma, Geneva, Verdana, sans-serif'
    }}>
      {/* Permission Modal */}
      {showPermissionPrompt && (
        <div style={{
          position: 'fixed', 
          top: 0, 
          left: 0, 
          width: '100vw', 
          height: '100vh',
          background: 'rgba(0, 0, 0, 0.8)', 
          zIndex: 1000, 
          display: 'flex', 
          alignItems: 'center', 
          justifyContent: 'center'
        }}>
          <div style={{
            background: '#ffffff', 
            borderRadius: 8, 
            padding: 32, 
            boxShadow: '0 4px 16px rgba(0, 0, 0, 0.3)', 
            maxWidth: 400, 
            textAlign: 'center'
          }}>
            <div style={{ 
              width: 64,
              height: 64,
              background: '#0078d4',
              borderRadius: '50%',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              margin: '0 auto 20px',
            }}>
              <svg width="28" height="28" viewBox="0 0 24 24" fill="white">
                <path d="M17 10.5V7c0-.55-.45-1-1-1H4c-.55 0-1 .45-1 1v10c0 .55.45 1 1 1h12c.55 0 1-.45 1-1v-3.5l4 4v-11l-4 4z"/>
              </svg>
            </div>
            <h2 style={{ margin: '0 0 12px 0', color: '#323130', fontSize: 20, fontWeight: 600 }}>
              Join Meeting
            </h2>
            <p style={{ margin: '0 0 24px 0', color: '#605e5c', lineHeight: 1.4, fontSize: 14 }}>
              Please allow camera and microphone access to join the meeting
            </p>
            <button 
              onClick={handleAllowPermissions} 
              style={{ 
                background: '#0078d4',
                color: 'white',
                border: 'none',
                borderRadius: 4,
                fontSize: 14, 
                fontWeight: 600,
                padding: '12px 24px',
                cursor: 'pointer',
                minWidth: 120,
              }}
            >
              Join now
            </button>
          </div>
        </div>
      )}

      {/* Header */}
      <div style={{
        background: '#292929',
        padding: '16px 24px',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
        borderBottom: '1px solid #464646'
      }}>
        <div style={{ display: 'flex', alignItems: 'center', gap: 12 }}>
          <div style={{ 
            width: 32, 
            height: 32, 
            background: '#0078d4', 
            borderRadius: 4,
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
          }}>
            <svg width="18" height="18" viewBox="0 0 24 24" fill="white">
              <path d="M17 10.5V7c0-.55-.45-1-1-1H4c-.55 0-1 .45-1 1v10c0 .55.45 1 1 1h12c.55 0 1-.45 1-1v-3.5l4 4v-11l-4 4z"/>
            </svg>
          </div>
          <div>
            <h1 style={{ margin: 0, color: '#ffffff', fontSize: 16, fontWeight: 600 }}>
              Meeting Room
            </h1>
            <p style={{ margin: 0, color: '#c4c4c4', fontSize: 12 }}>
              {roomId.length > 30 ? `${roomId.substring(0, 30)}...` : roomId}
            </p>
          </div>
        </div>
        <div style={{ 
          color: '#ffffff', 
          fontSize: 14,
          fontWeight: 500,
        }}>
          {participantGrid.length} participant{participantGrid.length !== 1 ? 's' : ''}
        </div>
      </div>

      {/* Main content area */}
      <div style={{ flex: 1, padding: 24 }}>
        {/* Video grid */}
        <div
          style={{
            display: 'grid',
            gridTemplateColumns: getGridColumns(),
            gap: 16,
            alignItems: 'center',
            justifyItems: 'center',
            maxWidth: 1400,
            margin: '0 auto',
            width: '100%',
          }}
        >
          {participantGrid.length === 0 && (
            <div style={{ 
              color: '#c4c4c4', 
              textAlign: 'center', 
              gridColumn: '1/-1',
              fontSize: 16,
            }}>
              Waiting for participants...
            </div>
          )}
          {participantGrid.map(renderParticipant)}
        </div>
      </div>

      {/* Controls bar */}
      <div style={{
        background: '#292929',
        padding: '16px',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        gap: 8,
        borderTop: '1px solid #464646'
      }}>
        <button 
          onClick={toggleMic}
          style={{
            width: 48,
            height: 48,
            borderRadius: '50%',
            border: 'none',
            background: micOn ? '#484644' : '#cc4125',
            color: '#ffffff',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            cursor: 'pointer',
            transition: 'background-color 0.2s',
          }}
          title={micOn ? 'Mute microphone' : 'Unmute microphone'}
        >
          <svg width="20" height="20" viewBox="0 0 24 24" fill="currentColor">
            {micOn ? (
              <path d="M12 14c1.66 0 2.99-1.34 2.99-3L15 5c0-1.66-1.34-3-3-3S9 3.34 9 5v6c0 1.66 1.34 3 3 3zm5.3-3c0 3-2.54 5.1-5.3 5.1S6.7 14 6.7 11H5c0 3.41 2.72 6.23 6 6.72V21h2v-3.28c3.28-.48 6-3.3 6-6.72h-1.7z"/>
            ) : (
              <>
                <path d="M19 11h-1.7c0 .74-.16 1.43-.43 2.05l1.23 1.23c.56-.98.9-2.09.9-3.28zm-4.02.17c0-.06.02-.11.02-.17V5c0-1.66-1.34-3-3-3S9 3.34 9 5v.18l5.98 5.99zM4.27 3L3 4.27l6.01 6.01V11c0 1.66 1.33 3 2.99 3 .22 0 .44-.03.65-.08l1.66 1.66c-.71.33-1.5.52-2.31.52-2.76 0-5.3-2.1-5.3-5.1H5c0 3.41 2.72 6.23 6 6.72V21h2v-3.28c.91-.13 1.77-.45 2.54-.9L19.73 21 21 19.73 4.27 3z"/>
              </>
            )}
          </svg>
        </button>

        <button 
          onClick={toggleCam}
          style={{
            width: 48,
            height: 48,
            borderRadius: '50%',
            border: 'none',
            background: camOn ? '#484644' : '#cc4125',
            color: '#ffffff',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            cursor: 'pointer',
            transition: 'background-color 0.2s',
          }}
          title={camOn ? 'Turn off camera' : 'Turn on camera'}
        >
          <svg width="20" height="20" viewBox="0 0 24 24" fill="currentColor">
            {camOn ? (
              <path d="M17 10.5V7c0-.55-.45-1-1-1H4c-.55 0-1 .45-1 1v10c0 .55.45 1 1 1h12c.55 0 1-.45 1-1v-3.5l4 4v-11l-4 4z"/>
            ) : (
              <>
                <path d="M21 6.5l-4 4V7c0-.55-.45-1-1-1H9.82L21 17.18V6.5zM3.27 2L2 3.27 4.73 6H4c-.55 0-1 .45-1 1v10c0 .55.45 1 1 1h12c.21 0 .39-.08.54-.18L19.73 21 21 19.73 3.27 2z"/>
              </>
            )}
          </svg>
        </button>

        <button 
          onClick={startScreenShare}
          disabled={screenSharing}
          style={{
            width: 48,
            height: 48,
            borderRadius: '50%',
            border: 'none',
            background: screenSharing ? '#0078d4' : '#484644',
            color: '#ffffff',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            cursor: screenSharing ? 'default' : 'pointer',
            opacity: screenSharing ? 1 : 0.9,
            transition: 'background-color 0.2s',
          }}
          title="Share screen"
        >
          <svg width="20" height="20" viewBox="0 0 24 24" fill="currentColor">
            <path d="M20 18c1.1 0 1.99-.9 1.99-2L22 6c0-1.1-.9-2-2-2H4c-1.1 0-2 .9-2 2v10c0 1.1.9 2 2 2H0v2h24v-2h-4zM4 6h16v10H4V6z"/>
          </svg>
        </button>

        <button 
          onClick={takeScreenshot}
          style={{
            width: 48,
            height: 48,
            borderRadius: '50%',
            border: 'none',
            background: '#484644',
            color: '#ffffff',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            cursor: 'pointer',
            transition: 'background-color 0.2s',
          }}
          title="Take screenshot"
        >
          <svg width="20" height="20" viewBox="0 0 24 24" fill="currentColor">
            <circle cx="12" cy="12" r="3.2"/>
            <path d="M9 2 7.17 4H4c-1.1 0-2 .9-2 2v12c0 1.1.9 2 2 2h16c1.1 0 2-.9 2-2V6c0-1.1-.9-2-2-2h-3.17L15 2H9zm3 15c-2.76 0-5-2.24-5-5s2.24-5 5-5 5 2.24 5 5-2.24 5-5 5z"/>
          </svg>
        </button>

        <div style={{ 
          width: 1, 
          height: 32, 
          background: '#464646', 
          margin: '0 8px',
        }} />

        <button 
          onClick={leaveRoom}
          style={{
            width: 48,
            height: 48,
            borderRadius: '50%',
            border: 'none',
            background: '#cc4125',
            color: '#ffffff',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            cursor: 'pointer',
            transition: 'background-color 0.2s',
          }}
          title="Leave meeting"
        >
          <svg width="20" height="20" viewBox="0 0 24 24" fill="currentColor">
            <path d="M6 2c-1.1 0-2 .9-2 2v16c0 1.1.9 2 2 2h6v-2H6V4h6V2H6zm10.5 11l-2.5 2.5 1.5 1.5L20 12l-4.5-5-1.5 1.5L16.5 11H9v2h7.5z"/>
          </svg>
        </button>
      </div>

      {/* Error notification */}
      {error && (
        <div style={{
          position: 'fixed',
          top: 80,
          right: 24,
          background: '#cc4125',
          color: '#ffffff',
          padding: '12px 16px',
          borderRadius: 4,
          fontSize: 14,
          fontWeight: 500,
          boxShadow: '0 4px 12px rgba(0, 0, 0, 0.3)',
          zIndex: 1000
        }}>
          {error}
        </div>
      )}
    </div>
  );
}

export default Room;
