import React, { useEffect, useRef, useState } from 'react';
import io from 'socket.io-client';
import { v4 as uuidv4 } from 'uuid';
import html2canvas from 'html2canvas';

const SIGNAL_SERVER = 'http://localhost:5000';

function Room({ roomId }) {
  const [participants, setParticipants] = useState([]);
  const [remoteStreams, setRemoteStreams] = useState({}); // userId -> MediaStream
  const [micOn, setMicOn] = useState(true);
  const [camOn, setCamOn] = useState(true);
  const [screenSharing, setScreenSharing] = useState(false);
  const [error, setError] = useState('');
  const [showPermissionPrompt, setShowPermissionPrompt] = useState(true);
  const localVideoRef = useRef();
  const socketRef = useRef();
  const peersRef = useRef({}); // userId -> RTCPeerConnection
  const userId = useRef(uuidv4());
  const localStream = useRef();
  const screenStream = useRef();

  useEffect(() => {
    socketRef.current = io(SIGNAL_SERVER);
    socketRef.current.emit('join', { roomId, userId: userId.current });
    socketRef.current.on('participants', (list) => {
      setParticipants(list);
      // Create peer connections to all existing participants (except self)
      list.forEach(existingId => {
        if (existingId !== userId.current && !peersRef.current[existingId]) {
          const peer = createPeer(existingId, true);
          peersRef.current[existingId] = peer;
        }
      });
    });
    socketRef.current.on('signal', handleSignal);
    socketRef.current.on('error', setError);
    socketRef.current.on('new-user', ({ userId: newUserId }) => {
      // All existing users create a peer connection to the new user
      if (newUserId !== userId.current && !peersRef.current[newUserId]) {
        const peer = createPeer(newUserId, true);
        peersRef.current[newUserId] = peer;
      }
    });
    return () => {
      socketRef.current.emit('leave', { roomId, userId: userId.current });
      socketRef.current.disconnect();
      Object.values(peersRef.current).forEach(peer => peer.close());
    };
    // eslint-disable-next-line
  }, []);

  useEffect(() => {
    if (!roomId) return;
    setShowPermissionPrompt(true);
    // Do not call startLocalStream here!
    // Request camera/mic permissions and start local stream only when joining a new meeting
    // eslint-disable-next-line
  }, [roomId]);

  const handleAllowPermissions = async () => {
    setShowPermissionPrompt(false);
    await startLocalStream();
  };

  const startLocalStream = async () => {
    try {
      localStream.current = await navigator.mediaDevices.getUserMedia({ video: true, audio: true });
    } catch (e) {
      setError('Could not access camera/mic');
    }
  };

  // Handle new user: create a peer connection for them
  const handleNewUser = async ({ userId: newUserId }) => {
    if (newUserId === userId.current) return;
    if (!peersRef.current[newUserId]) {
      const peer = createPeer(newUserId, true);
      peersRef.current[newUserId] = peer;
    }
  };

  // Helper to determine if we are polite (lexicographically greater userId)
  const isPolite = (otherUserId) => userId.current > otherUserId;

  // Create peer connection for a user
  const createPeer = (otherUserId, initiator) => {
    const peer = new window.RTCPeerConnection();
    if (localStream.current) {
      localStream.current.getTracks().forEach(track => peer.addTrack(track, localStream.current));
    }
    peer.ontrack = (e) => {
      setRemoteStreams(prev => ({
        ...prev,
        [otherUserId]: e.streams[0]
      }));
    };
    peer.onicecandidate = (e) => {
      if (e.candidate) {
        socketRef.current.emit('signal', { roomId, userId: userId.current, to: otherUserId, signal: { candidate: e.candidate } });
      }
    };
    if (initiator) {
      peer.createOffer().then(offer => {
        peer.setLocalDescription(offer);
        socketRef.current.emit('signal', { roomId, userId: userId.current, to: otherUserId, signal: { sdp: offer } });
      });
    }
    return peer;
  };

  // Handle incoming signal with polite peer logic
  const handleSignal = async ({ userId: from, signal }) => {
    if (from === userId.current) return;
    let peer = peersRef.current[from];
    if (!peer) {
      peer = createPeer(from, false);
      peersRef.current[from] = peer;
    }
    try {
      if (signal.sdp) {
        const desc = new RTCSessionDescription(signal.sdp);
        const isOffer = desc.type === 'offer';
        const polite = isPolite(from);

        if (isOffer) {
          const signalingState = peer.signalingState;
          if (signalingState !== 'stable') {
            if (!polite) {
              // Impolite peer ignores glare offer
              return;
            }
            // Polite peer rolls back and accepts the offer
            await peer.setLocalDescription({ type: 'rollback' });
          }
          await peer.setRemoteDescription(desc);
          const answer = await peer.createAnswer();
          await peer.setLocalDescription(answer);
          socketRef.current.emit('signal', { roomId, userId: userId.current, to: from, signal: { sdp: answer } });
        } else {
          await peer.setRemoteDescription(desc);
        }
      } else if (signal.candidate) {
        try {
          await peer.addIceCandidate(new RTCIceCandidate(signal.candidate));
        } catch (err) {
          // Ignore ICE candidate errors
        }
      }
    } catch (err) {
      // Optionally log or handle error
    }
  };

  const toggleMic = () => {
    localStream.current.getAudioTracks().forEach(track => (track.enabled = !micOn));
    setMicOn(!micOn);
  };

  const toggleCam = () => {
    localStream.current.getVideoTracks().forEach(track => (track.enabled = !camOn));
    setCamOn(!camOn);
  };

  const leaveRoom = () => {
    socketRef.current.emit('leave', { roomId, userId: userId.current });
    window.location.reload();
  };

  const startScreenShare = async () => {
    try {
      screenStream.current = await navigator.mediaDevices.getDisplayMedia({ video: true });
      const screenTrack = screenStream.current.getVideoTracks()[0];
      const sender = peerRef.current.getSenders().find(s => s.track.kind === 'video');
      sender.replaceTrack(screenTrack);
      setScreenSharing(true);
      screenTrack.onended = () => {
        sender.replaceTrack(localStream.current.getVideoTracks()[0]);
        setScreenSharing(false);
      };
    } catch (e) {
      setError('Screen sharing failed');
    }
  };

  const takeScreenshot = async () => {
    if (!localVideoRef.current) return;
    const canvas = await html2canvas(localVideoRef.current);
    const link = document.createElement('a');
    link.href = canvas.toDataURL('image/png');
    link.download = 'screenshot.png';
    link.click();
  };

  // Helper for initials
  const getInitials = name =>
    name
      .split(' ')
      .map(n => n[0])
      .join('')
      .toUpperCase();

  // Responsive grid columns
  const getGridColumns = () => {
    const count = participantGrid.length;
    if (count === 1) return '1fr';
    if (count === 2) return 'repeat(2, 1fr)';
    if (count <= 4) return 'repeat(2, 1fr)';
    if (count <= 6) return 'repeat(3, 1fr)';
    if (count <= 9) return 'repeat(3, 1fr)';
    return 'repeat(4, 1fr)';
  };

  // Compose participant list for grid (local + remote)
  const participantGrid = [
    {
      id: userId.current,
      name: 'You',
      cameraOn: camOn && localStream.current,
      isLocal: true,
      avatarUrl: null,
      stream: localStream.current,
    },
    ...participants
      .filter(p => p !== userId.current && typeof p === 'string')
      .map(name => ({
        id: name,
        name,
        cameraOn: !!remoteStreams[name],
        isLocal: false,
        avatarUrl: null,
        stream: remoteStreams[name] || null,
      })),
  ];

  // Generate random avatar colors
  const getAvatarColor = (name) => {
    const colors = [
      '#FF6B6B', '#4ECDC4', '#45B7D1', '#96CEB4', '#FECA57',
      '#FF9FF3', '#54A0FF', '#5F27CD', '#00D2D3', '#FF9F43',
      '#2ED573', '#3742FA', '#F8B500', '#EE5A52', '#0abde3'
    ];
    const index = name.charCodeAt(0) % colors.length;
    return colors[index];
  };

  // Render participant tile
  const renderParticipant = (user, idx) => (
    <div
      key={user.id || user.name || idx}
      style={{
        background: '#0F0F23',
        borderRadius: 16,
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        minWidth: participantGrid.length === 1 ? 400 : 280,
        minHeight: participantGrid.length === 1 ? 300 : 200,
        width: '100%',
        position: 'relative',
        overflow: 'hidden',
        border: user.isLocal ? '3px solid #00C9FF' : '2px solid #1e1e3f',
        boxShadow: user.isLocal 
          ? '0 8px 32px rgba(0, 201, 255, 0.3)' 
          : '0 8px 24px rgba(0,0,0,0.4)',
        transition: 'all 0.3s ease',
      }}
    >
      {/* Show video if camera is on and stream is available */}
      {user.cameraOn && user.stream && (
        <video
          ref={el => {
            if (el && user.stream) {
              el.srcObject = user.stream;
              if (user.isLocal) localVideoRef.current = el;
            }
          }}
          autoPlay
          muted={user.isLocal}
          playsInline
          style={{
            width: '100%',
            height: '100%',
            objectFit: 'cover',
            borderRadius: 13,
          }}
        />
      )}
      
      {/* If camera is off or no stream, show avatar */}
      {(!user.cameraOn || !user.stream) && (
        <div style={{
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          justifyContent: 'center',
          height: '100%',
          color: '#fff'
        }}>
          {user.avatarUrl ? (
            <img
              src={user.avatarUrl}
              alt={user.name}
              style={{
                width: participantGrid.length === 1 ? 120 : 80,
                height: participantGrid.length === 1 ? 120 : 80,
                borderRadius: '50%',
                marginBottom: 12,
                objectFit: 'cover',
                border: '4px solid rgba(255,255,255,0.2)',
              }}
            />
          ) : (
            <div
              style={{
                width: participantGrid.length === 1 ? 120 : 80,
                height: participantGrid.length === 1 ? 120 : 80,
                borderRadius: '50%',
                marginBottom: 12,
                background: `linear-gradient(135deg, ${getAvatarColor(user.name)}, ${getAvatarColor(user.name)}CC)`,
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: participantGrid.length === 1 ? 36 : 28,
                fontWeight: 700,
                color: '#fff',
                border: '4px solid rgba(255,255,255,0.3)',
                boxShadow: '0 4px 16px rgba(0,0,0,0.3)',
              }}
            >
              {getInitials(user.name || '')}
            </div>
          )}
          <div style={{
            fontSize: participantGrid.length === 1 ? 18 : 14,
            fontWeight: 500,
            color: 'rgba(255,255,255,0.9)',
            textAlign: 'center'
          }}>
            Camera is off
          </div>
        </div>
      )}
      
      {/* Status indicators */}
      <div style={{
        position: 'absolute',
        top: 12,
        right: 12,
        display: 'flex',
        gap: 8,
      }}>
        {user.isLocal && !micOn && (
          <div style={{
            width: 32,
            height: 32,
            borderRadius: '50%',
            background: 'linear-gradient(135deg, #FF4757, #FF3838)',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontSize: 14,
            border: '2px solid rgba(255,255,255,0.9)',
            boxShadow: '0 2px 8px rgba(255, 71, 87, 0.4)',
          }}>
            <svg width="16" height="16" viewBox="0 0 24 24" fill="white">
              <path d="M16 8c0-2.21-1.79-4-4-4S8 5.79 8 8v4c0 2.21 1.79 4 4 4s4-1.79 4-4V8z"/>
              <path d="M19 10v2c0 3.87-3.13 7-7 7s-7-3.13-7-7v-2h2v2c0 2.76 2.24 5 5 5s5-2.24 5-5v-2h2z"/>
              <line x1="2" y1="2" x2="22" y2="22" stroke="white" strokeWidth="2"/>
            </svg>
          </div>
        )}
        {user.isLocal && micOn && (
          <div style={{
            width: 32,
            height: 32,
            borderRadius: '50%',
            background: 'linear-gradient(135deg, #2ED573, #20BF6B)',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontSize: 14,
            border: '2px solid rgba(255,255,255,0.9)',
            boxShadow: '0 2px 8px rgba(46, 213, 115, 0.4)',
          }}>
            <svg width="16" height="16" viewBox="0 0 24 24" fill="white">
              <path d="M16 8c0-2.21-1.79-4-4-4S8 5.79 8 8v4c0 2.21 1.79 4 4 4s4-1.79 4-4V8z"/>
              <path d="M19 10v2c0 3.87-3.13 7-7 7s-7-3.13-7-7v-2h2v2c0 2.76 2.24 5 5 5s5-2.24 5-5v-2h2z"/>
            </svg>
          </div>
        )}
      </div>

      {/* Name overlay */}
      <div
        style={{
          position: 'absolute',
          bottom: 0,
          left: 0,
          right: 0,
          background: user.cameraOn && user.stream 
            ? 'linear-gradient(transparent, rgba(0,0,0,0.8))' 
            : 'transparent',
          color: '#fff',
          padding: user.cameraOn && user.stream ? '24px 16px 12px' : '8px 16px',
          fontSize: 16,
          fontWeight: 600,
          textAlign: 'center',
          textShadow: '0 2px 4px rgba(0,0,0,0.5)',
        }}
      >
        {user.name}
      </div>
    </div>
  );

  useEffect(() => {
    if (!localStream.current) return;
    // For each peer, add tracks if not already present and renegotiate
    Object.entries(peersRef.current).forEach(([peerId, peer]) => {
      // Add tracks if not already present
      const senders = peer.getSenders();
      localStream.current.getTracks().forEach(track => {
        if (!senders.find(sender => sender.track && sender.track.kind === track.kind)) {
          peer.addTrack(track, localStream.current);
        }
      });
      // Renegotiate (create and send new offer)
      peer.createOffer().then(offer => {
        peer.setLocalDescription(offer);
        socketRef.current.emit('signal', { roomId, userId: userId.current, to: peerId, signal: { sdp: offer } });
      });
    });
  }, [localStream.current]);

  const controlButtonStyle = {
    width: 56,
    height: 56,
    borderRadius: '50%',
    border: 'none',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    fontSize: 18,
    cursor: 'pointer',
    transition: 'all 0.3s ease',
    fontWeight: 600,
    position: 'relative',
    overflow: 'hidden',
  };

  return (
    <div style={{ 
      minHeight: '100vh', 
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      display: 'flex',
      flexDirection: 'column',
      fontFamily: '"Inter", "Segoe UI", Tahoma, Geneva, Verdana, sans-serif'
    }}>
      {/* Permission Modal */}
      {showPermissionPrompt && (
        <div style={{
          position: 'fixed', 
          top: 0, 
          left: 0, 
          width: '100vw', 
          height: '100vh',
          background: 'rgba(0,0,0,0.85)', 
          zIndex: 1000, 
          display: 'flex', 
          alignItems: 'center', 
          justifyContent: 'center',
          backdropFilter: 'blur(10px)',
        }}>
          <div style={{
            background: 'linear-gradient(135deg, #667eea, #764ba2)', 
            borderRadius: 20, 
            padding: 48, 
            boxShadow: '0 20px 60px rgba(0,0,0,0.4)', 
            maxWidth: 450, 
            textAlign: 'center',
            border: '1px solid rgba(255,255,255,0.2)',
          }}>
            <div style={{ 
              fontSize: 64, 
              marginBottom: 24,
              background: 'linear-gradient(135deg, #00C9FF, #92FE9D)',
              borderRadius: '50%',
              width: 100,
              height: 100,
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              margin: '0 auto 24px',
            }}>
              <svg width="40" height="40" viewBox="0 0 24 24" fill="white">
                <path d="M17 10.5V7c0-.55-.45-1-1-1H4c-.55 0-1 .45-1 1v10c0 .55.45 1 1 1h12c.55 0 1-.45 1-1v-3.5l4 4v-11l-4 4z"/>
              </svg>
            </div>
            <h2 style={{ margin: '0 0 16px 0', color: '#fff', fontSize: 28, fontWeight: 700 }}>
              Join Meeting
            </h2>
            <p style={{ margin: '0 0 32px 0', color: 'rgba(255,255,255,0.9)', lineHeight: 1.6, fontSize: 16 }}>
              We need access to your camera and microphone to connect you with others in this meeting.
            </p>
            <button 
              onClick={handleAllowPermissions} 
              style={{ 
                background: 'linear-gradient(135deg, #00C9FF, #92FE9D)',
                color: 'white',
                border: 'none',
                borderRadius: 12,
                fontSize: 18, 
                fontWeight: 700,
                padding: '16px 40px',
                cursor: 'pointer',
                transition: 'all 0.3s ease',
                boxShadow: '0 8px 24px rgba(0, 201, 255, 0.3)',
                textTransform: 'uppercase',
                letterSpacing: '1px',
              }}
              onMouseOver={(e) => {
                e.target.style.transform = 'translateY(-2px)';
                e.target.style.boxShadow = '0 12px 32px rgba(0, 201, 255, 0.4)';
              }}
              onMouseOut={(e) => {
                e.target.style.transform = 'translateY(0)';
                e.target.style.boxShadow = '0 8px 24px rgba(0, 201, 255, 0.3)';
              }}
            >
              Join Now
            </button>
          </div>
        </div>
      )}

      {/* Header */}
      <div style={{
        background: 'rgba(15, 15, 35, 0.9)',
        backdropFilter: 'blur(20px)',
        padding: '20px 32px',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between',
        borderBottom: '1px solid rgba(255,255,255,0.1)'
      }}>
        <div style={{ display: 'flex', alignItems: 'center', gap: 16 }}>
          <div style={{ 
            width: 48, 
            height: 48, 
            background: 'linear-gradient(135deg, #00C9FF, #92FE9D)', 
            borderRadius: 12,
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontSize: 24,
            boxShadow: '0 4px 16px rgba(0, 201, 255, 0.3)',
          }}>
            <svg width="24" height="24" viewBox="0 0 24 24" fill="white">
              <path d="M17 10.5V7c0-.55-.45-1-1-1H4c-.55 0-1 .45-1 1v10c0 .55.45 1 1 1h12c.55 0 1-.45 1-1v-3.5l4 4v-11l-4 4z"/>
            </svg>
          </div>
          <div>
            <h1 style={{ margin: 0, color: '#fff', fontSize: 24, fontWeight: 700 }}>
              Meeting Room
            </h1>
            <p style={{ margin: 0, color: 'rgba(255,255,255,0.7)', fontSize: 14 }}>
              Room ID: {roomId.length > 20 ? `${roomId.substring(0, 20)}...` : roomId}
            </p>
          </div>
        </div>
        <div style={{ 
          color: 'rgba(255,255,255,0.9)', 
          fontSize: 16,
          background: 'rgba(255,255,255,0.1)',
          padding: '8px 16px',
          borderRadius: 20,
          fontWeight: 600,
        }}>
          {participantGrid.length} participant{participantGrid.length !== 1 ? 's' : ''}
        </div>
      </div>

      {/* Main content area */}
      <div style={{ flex: 1, display: 'flex', flexDirection: 'column', padding: 32 }}>
        {/* Video grid */}
        <div
          style={{
            display: 'grid',
            gridTemplateColumns: getGridColumns(),
            gap: 24,
            alignItems: 'center',
            justifyItems: 'center',
            maxWidth: 1400,
            margin: '0 auto',
            width: '100%',
            flex: 1,
          }}
        >
          {participantGrid.length === 0 && (
            <div style={{ 
              color: 'rgba(255,255,255,0.8)', 
              textAlign: 'center', 
              gridColumn: '1/-1',
              fontSize: 20,
              fontWeight: 500,
            }}>
              Waiting for participants to join...
            </div>
          )}
          {participantGrid.map(renderParticipant)}
        </div>
      </div>

      {/* Controls bar */}
      <div style={{
        background: 'rgba(15, 15, 35, 0.95)',
        backdropFilter: 'blur(20px)',
        padding: '20px 32px',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        gap: 20,
        borderTop: '1px solid rgba(255,255,255,0.1)'
      }}>
        <button 
          onClick={toggleMic}
          style={{
            ...controlButtonStyle,
            background: micOn 
              ? 'linear-gradient(135deg, #2ED573, #20BF6B)' 
              : 'linear-gradient(135deg, #FF4757, #FF3838)',
            color: '#fff',
            boxShadow: micOn 
              ? '0 6px 20px rgba(46, 213, 115, 0.4)' 
              : '0 6px 20px rgba(255, 71, 87, 0.4)',
          }}
          onMouseOver={(e) => {
            e.target.style.transform = 'translateY(-3px)';
            e.target.style.boxShadow = micOn 
              ? '0 8px 25px rgba(46, 213, 115, 0.6)' 
              : '0 8px 25px rgba(255, 71, 87, 0.6)';
          }}
          onMouseOut={(e) => {
            e.target.style.transform = 'translateY(0)';
            e.target.style.boxShadow = micOn 
              ? '0 6px 20px rgba(46, 213, 115, 0.4)' 
              : '0 6px 20px rgba(255, 71, 87, 0.4)';
          }}
          title={micOn ? 'Mute microphone' : 'Unmute microphone'}
        >
          <svg width="20" height="20" viewBox="0 0 24 24" fill="currentColor">
            {micOn ? (
              <path d="M16 8c0-2.21-1.79-4-4-4S8 5.79 8 8v4c0 2.21 1.79 4 4 4s4-1.79 4-4V8zM19 10v2c0 3.87-3.13 7-7 7s-7-3.13-7-7v-2h2v2c0 2.76 2.24 5 5 5s5-2.24 5-5v-2h2z"/>
            ) : (
              <>
                <path d="M16 8c0-2.21-1.79-4-4-4S8 5.79 8 8v4c0 2.21 1.79 4 4 4s4-1.79 4-4V8z"/>
                <path d="M19 10v2c0 3.87-3.13 7-7 7s-7-3.13-7-7v-2h2v2c0 2.76 2.24 5 5 5s5-2.24 5-5v-2h2z"/>
                <line x1="2" y1="2" x2="22" y2="22" stroke="currentColor" strokeWidth="2"/>
              </>
            )}
          </svg>
        </button>

        <button 
          onClick={toggleCam}
          style={{
            ...controlButtonStyle,
            background: camOn 
              ? 'linear-gradient(135deg, #3742FA, #2F3A8F)' 
              : 'linear-gradient(135deg, #FF4757, #FF3838)',
            color: '#fff',
            boxShadow: camOn 
              ? '0 6px 20px rgba(55, 66, 250, 0.4)' 
              : '0 6px 20px rgba(255, 71, 87, 0.4)',
          }}
          onMouseOver={(e) => {
            e.target.style.transform = 'translateY(-3px)';
            e.target.style.boxShadow = camOn 
              ? '0 8px 25px rgba(55, 66, 250, 0.6)' 
              : '0 8px 25px rgba(255, 71, 87, 0.6)';
          }}
          onMouseOut={(e) => {
            e.target.style.transform = 'translateY(0)';
            e.target.style.boxShadow = camOn 
              ? '0 6px 20px rgba(55, 66, 250, 0.4)' 
              : '0 6px 20px rgba(255, 71, 87, 0.4)';
          }}
          title={camOn ? 'Turn off camera' : 'Turn on camera'}
        >
          <svg width="20" height="20" viewBox="0 0 24 24" fill="currentColor">
            {camOn ? (
              <path d="M17 10.5V7c0-.55-.45-1-1-1H4c-.55 0-1 .45-1 1v10c0 .55.45 1 1 1h12c.55 0 1-.45 1-1v-3.5l4 4v-11l-4 4z"/>
            ) : (
              <>
                <path d="M17 10.5V7c0-.55-.45-1-1-1H4c-.55 0-1 .45-1 1v10c0 .55.45 1 1 1h12c.55 0 1-.45 1-1v-3.5l4 4v-11l-4 4z"/>
                <line x1="2" y1="2" x2="22" y2="22" stroke="currentColor" strokeWidth="2"/>
              </>
            )}
          </svg>
        </button>

        <button 
          onClick={startScreenShare}
          disabled={screenSharing}
          style={{
            ...controlButtonStyle,
            background: screenSharing 
              ? 'linear-gradient(135deg, #00C9FF, #92FE9D)' 
              : 'linear-gradient(135deg, #45B7D1, #4A90E2)',
            color: '#fff',
            opacity: screenSharing ? 1 : 0.9,
            boxShadow: '0 6px 20px rgba(69, 183, 209, 0.4)',
          }}
          onMouseOver={(e) => {
            if (!screenSharing) {
              e.target.style.transform = 'translateY(-3px)';
              e.target.style.boxShadow = '0 8px 25px rgba(69, 183, 209, 0.6)';
            }
          }}
          onMouseOut={(e) => {
            if (!screenSharing) {
              e.target.style.transform = 'translateY(0)';
              e.target.style.boxShadow = '0 6px 20px rgba(69, 183, 209, 0.4)';
            }
          }}
          title="Share screen"
        >
          <svg width="20" height="20" viewBox="0 0 24 24" fill="currentColor">
            <path d="M20 18c1.1 0 1.99-.9 1.99-2L22 6c0-1.1-.9-2-2-2H4c-1.1 0-2 .9-2 2v10c0 1.1.9 2 2 2H0v2h24v-2h-4zM4 6h16v10H4V6z"/>
            {screenSharing && <circle cx="12" cy="11" r="3" fill="currentColor"/>}
          </svg>
        </button>

        <button 
          onClick={takeScreenshot}
          style={{
            ...controlButtonStyle,
            background: 'linear-gradient(135deg, #FECA57, #FF9F43)',
            color: '#fff',
            boxShadow: '0 6px 20px rgba(254, 202, 87, 0.4)',
          }}
          onMouseOver={(e) => {
            e.target.style.transform = 'translateY(-3px)';
            e.target.style.boxShadow = '0 8px 25px rgba(254, 202, 87, 0.6)';
          }}
          onMouseOut={(e) => {
            e.target.style.transform = 'translateY(0)';
            e.target.style.boxShadow = '0 6px 20px rgba(254, 202, 87, 0.4)';
          }}
          title="Take screenshot"
        >
          <svg width="20" height="20" viewBox="0 0 24 24" fill="currentColor">
            <circle cx="12" cy="12" r="3.2"/>
            <path d="M9 2 7.17 4H4c-1.1 0-2 .9-2 2v12c0 1.1.9 2 2 2h16c1.1 0 2-.9 2-2V6c0-1.1-.9-2-2-2h-3.17L15 2H9zm3 15c-2.76 0-5-2.24-5-5s2.24-5 5-5 5 2.24 5 5-2.24 5-5 5z"/>
          </svg>
        </button>

        <div style={{ 
          width: 2, 
          height: 40, 
          background: 'linear-gradient(135deg, rgba(255,255,255,0.2), rgba(255,255,255,0.1))', 
          margin: '0 8px',
          borderRadius: 1,
        }} />

        <button 
          onClick={leaveRoom}
          style={{
            ...controlButtonStyle,
            background: 'linear-gradient(135deg, #FF4757, #C44569)',
            color: '#fff',
            boxShadow: '0 6px 20px rgba(255, 71, 87, 0.4)',
          }}
          onMouseOver={(e) => {
            e.target.style.transform = 'translateY(-3px)';
            e.target.style.boxShadow = '0 8px 25px rgba(255, 71, 87, 0.6)';
          }}
          onMouseOut={(e) => {
            e.target.style.transform = 'translateY(0)';
            e.target.style.boxShadow = '0 6px 20px rgba(255, 71, 87, 0.4)';
          }}
          title="Leave meeting"
        >
          <svg width="20" height="20" viewBox="0 0 24 24" fill="currentColor">
            <path d="M12 9c-1.6 0-3.15.25-4.6.72v3.1c0 .39-.23.74-.56.9-.98.49-1.87 1.12-2.66 1.85-.18.18-.43.28-.7.28-.28 0-.53-.11-.71-.29L.29 13.08c-.18-.17-.29-.42-.29-.7 0-.28.11-.53.29-.71C3.34 8.78 7.46 7 12 7s8.66 1.78 11.71 4.67c.18.18.29.43.29.71 0 .28-.11.53-.29.7l-2.48 2.48c-.18.18-.43.29-.71.29-.27 0-.52-.1-.7-.28-.79-.73-1.68-1.36-2.66-1.85-.33-.16-.56-.51-.56-.9v-3.1C15.15 9.25 13.6 9 12 9z"/>
          </svg>
        </button>
      </div>

      {/* Error notification */}
      {error && (
        <div style={{
          position: 'fixed',
          top: 100,
          right: 32,
          background: 'linear-gradient(135deg, #FF4757, #C44569)',
          color: '#fff',
          padding: '16px 20px',
          borderRadius: 12,
          fontSize: 16,
          fontWeight: 600,
          boxShadow: '0 8px 32px rgba(255, 71, 87, 0.4)',
          zIndex: 1000,
          border: '1px solid rgba(255,255,255,0.2)',
          backdropFilter: 'blur(10px)',
          animation: 'slideIn 0.3s ease',
        }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: 12 }}>
            <svg width="20" height="20" viewBox="0 0 24 24" fill="currentColor">
              <path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm-2 15l-5-5 1.41-1.41L10 14.17l7.59-7.59L19 8l-9 9z"/>
            </svg>
            {error}
          </div>
        </div>
      )}

      <style jsx>{`
        @keyframes slideIn {
          from { transform: translateX(100%); opacity: 0; }
          to { transform: translateX(0); opacity: 1; }
        }
      `}</style>
    </div>
  );
}

export default Room;
